import React from "react";
import ChartBar from "./ChartBar";
import "./Chart.css";

const Chart = (props) => {
  const dataPointValues = props.dataPoint.map((dataPoint) => dataPoint.value);
  const totalMax = Math.max(...dataPointValues);

  return (
    <div className="chart">
      {props.dataPoints.map((dataPoint) => (
        <ChartBar
          key={dataPoint.label}
          value={dataPoint.value}
          maxValue={totalMax}
          label={dataPoint.label}
        />
        // yukarida yaptigimiz sekilde ne kadar data varsa o kadar cubuk cikiyor.
        //Array value tutuyor. dataPoints in nasil data hold ettigini value={} ile biz belirliyoruz
      ))}
    </div>
  );
};

export default Chart;
