import React, { useState } from "react";
import "./ExpenseFilter.css";

function ExpenseFilter() {
  const [enteredYear, setEnteredYear] = useState(""); // useState yani State ile bilgileri kaydediyoruz

  function yearChangeHandler(event) {
    console.log(setEnteredYear(event.target.value));
  }

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter By Year</label>
        <select onChange={yearChangeHandler}>
          <option value={enteredYear}>2022</option>
          <option value={enteredYear}>2021</option>
          <option value={enteredYear}>2020</option>
          <option value={enteredYear}>2019</option>
        </select>
      </div>
    </div>
  );
}
export default ExpenseFilter;
